using System.Xml.Linq;
using OnlineShopWebApplication.Models;

namespace OnlineShopWebApplication
{
    public class ProductsInMemoryStorage : IProductsStorage
    {
        public List<Product> Products = new List<Product>
        {
            new Product("God of war", 2500, "Отправляйтесь в скандинавское царство\nОтомстив богам Олимпа, Кратос живет в царстве скандинавских божеств и чудовищ. В этом суровом беспощадном мире он должен не только самостоятельно бороться за выживание... но и научить этому сына.\n\nВторой шанс\nКратос снова отец. Будучи наставником и защитником сына, который стремится заслужить уважение отца, Кратос получил неожиданную возможность обуздать собственный гнев, который долгое время определял его поступки.\n\nПогружайтесь в мрачный мир грозных существ\nОт мраморных полов и роскошных колонн Олимпа до диких лесов, гор и пещер скандинавской мифологии задолго до появления викингов – вам предстоит оказаться в совершенно новых местах со своим пантеоном существ, чудовищ и богов.\n\nУчаствуйте в беспощадных сражениях\nС новой камерой за плечом главного героя игроки окажутся ближе к схваткам, чем когда-либо, а сами бои будут под стать скандинавским существам God of War™, с которыми столкнется Кратос: величественными, суровыми и неутомимыми. Новое основное оружие и способности хранят верность атмосфере серии God of War, давая возможность по-новому взглянуть на жестокие конфликты, присущие игровому жанру.", "~/img/games/god_of_war.jpeg"),
            new Product("Cyberpunk 2077", 2000, "Безумству мира поем мы славу! И он действительно сошел с ума, да так, что фантазии Уильяма Гибсона отдыхают. Моральные ценности утратили актуальность для людей. В моде цифровые наркотики и кибернизация организма. Первые дают возможность жить чужими эмоциями, как легальными: счастье, оргазм, эйфория, так и не совсем. Вторые превращают людей в полных роботов, которых можно брать под контроль и использовать в любых целях.\n\nВ гнездо разврата и нищеты превратился симпатичный некогда городок Night City. Здесь правят наркоторговцы, черные кибертрансплантологи, бандитские группировки. Хотите узнать, как выживают в этом кибергадюшнике простые люди, и попробовать изменить ситуацию к лучшему? Тогда поторопитесь купить Cyberpunk 2077 на steampay.com. Конец света уже не за горами!","~/img/games/cyberpunk_2077.jpg"),
            new Product("Detroit: Become Human", 950, "Detroit: Become Human, отмеченная наградами видеоигра от Quantic Dream, наконец доступна в Steam! В создании игры приняли участие всемирно известные актеры, среди которых: Джесси Уильямс («Анатомия страсти»), Клэнси Браун («Карнавал»), Лэнс Хенриксен («Чужие»), Брайан Декарт («Настоящая кровь») и Вэлори Керри («Сумерки»).\n\nЧТО ДЕЛАЕТ НАС ЛЮДЬМИ?\nДетройт, 2038 год. Технологии развились до такой степени, что человекообразные андроиды встречаются на каждом шагу. Они говорят, двигаются, ведут себя словно человеческие существа, но они — лишь машины в на службе у людей.", "~/img/games/detroit_become_human.jpg"),

            new Product("Red Dead Redemption 2", 2890, "Red Dead Redemption 2, грандиозная сага от Rockstar Games и самая высокооцененная игра на текущем поколении консолей, выходит на PC. В версии для PC представлены новые материалы для сюжетного режима, визуальные усовершенствования и другие нововведения.\r\n\r\nАмерика, 1899 год. Эпоха Дикого Запада подходит к концу. После сорвавшегося ограбления банка Артур Морган и другие подручные Датча ван дер Линде вынуждены пуститься в бега. Их банде предстоит участвовать в кражах, грабежах и перестрелках в самом сердце Америки. За ними по пятам идут федеральные агенты и лучшие в стране охотники за головами, а саму банду разрывают внутренние противоречия. Артуру предстоит выбрать, что для него важнее: его собственные идеалы или же верность людям, которые его взрастили.", "~/img/games/red_dead_redemption_2.jpg"),
            new Product("Divinity: Original Sin 2", 2200, "Divinity: Original Sin 2 является полноценным сюжетным продолжение оригинальной игры, построенным на движке Divinity Engine и получившим новый интерфейс, художественный стиль и продвинутую ролевую систему. При этом лучшие черты первой Original Sin остались на месте: вы вольны идти, куда захотите, можете убивать, кого заблагорассудится, искать бесчисленные пути для решения поставленных задач и собирать команду сопартийцев мечты.\r\n\r\nВ мире Divinity: Original Sin 2, где боги мертвы, вам выпадает роль персонажа, владеющего запрещенной магией. Находясь под карантином в тюремной колонии Форта «Радость», вы должны отстоять свое право на невероятные силы перед Магистрами, стремящимися «излечить» вас от пугающей их магии.\r\n\r\nПри этом у Ордена Магистров есть свои секреты — жуткие и кровавые, угрожающие всем и каждому. В мире, где боги мертвы, может наступить лишь хаос. Поэтому настало время для появления нового божества.","~/img/games/divinity_original_sin_2.jpg"),
            new Product("Death Stranding", 950, "Легендарный творец Хидэо Кодзима представляет окончательную версию DIRECTOR'S CUT знаковой для жанра игры.\r\nТаинственное событие, известное как Death Stranding, в будущем открыло дверь между миром живых и миром мертвых. Как результат, уродливые потусторонние твари отправились бродить по падшему миру, в котором почти не осталось людей.\r\nДействуя от лица Сэма Бриджеса и объединяя последних выживших на территории Америки, вам предстоит возродить надежду человечества.\r\nУдастся ли вам шаг за шагом воссоединить разрушенный мир?", "~/img/games/death_stranding.jpg"),

            new Product("Forza Horizon 4", 1900, "Времена года полностью меняют облик главного автомобильного фестиваля планеты. Исследуйте чудесные пейзажи и исторические достопримечательности Великобритании, путешествуя по открытому миру в одиночку или вместе с другими игроками. Собирайте, модифицируйте и испытывайте более 450 автомобилей. Участвуйте в гонках, выполняйте трюки, творите и исследуйте, чтобы стать суперзвездой Horizon.", "~/img/games/forza_horizon_4.jpg"),
            new Product("Northgard", 870, "Northgard — стратегия, вдохновленная скандинавской мифологией. Возглавьте клан викингов и покорите неизведанные земли, полные тайн.\r\n\r\n","~/img/games/northgard.jpg"),
            new Product("Atomic Heart", 2450, "ОБРАТНАЯ СТОРОНА ИДЕАЛЬНОГО МИРА\r\n1955 год. Советский Союз, победивший во Второй Мировой Войне, становится мировой технологической сверхдержавой благодаря развитию роботостроения. Всевозможные модели роботов существенно упростили жизнь людей.\r\n\r\nНаука, равенство и братство — вот основные символы современной реальности. Общество находится на пике прогресса с высочайшим уровнем жизни. Роботы под руководством ученых строят идеальные города, создают объекты культуры и помогают в исследовании мира и покорении стихий. \r\n\r\nНо какова цена безудержного прогресса и что скрывается за картиной идеального мира?\r\n\r\nРОБОТЫ, МУТАНТЫ И ЛАБОРАТОРНЫЕ ТВАРИ НА ТВОЕМ ПУТИ\r\nГражданские роботы, призванные помогать людям, восстали против своих создателей. Секретные эксперименты ученых стали результатом появления ужасных монстров-мутантов. Гигантские машины и кровожадные существа — все они станут преградой на пути к выполнению секретного задания. Необходимо приложить немало усилий и изучить каждого из опаснейших противников, чтобы не стать для них легкой добычей.\r\n\r\nЧтобы достигнуть цели, тебе придется изучить каждый уголок секретных лабораторий, исследовательских центров и научных полигонов, узнать, какие эксперименты проводили ученые, и каких результатов смогли достичь.\r\n\r\nИСПОЛЬЗУЙ ВСЕ ОРУЖИЕ И НАВЫКИ, ЧТОБЫ ВЫЖИТЬ\r\nСражаться придется много. Подбери свою лучшую тактику к каждому уникальному противнику, используй все, что «попадется под руку», начиная с особых возможностей боевой перчатки, заканчивая тяжелым вооружением массового поражения.\r\n\r\nКомбинируй свои защитные свойства и атакующие умения, взаимодействуй с окружением, не спускай глаз с врага — только так ты сможешь победить. Широкий спектр улучшений для любого оружия, огнестрельного и рукопашного, позволит разорвать на куски любого, кто встанет на твоем пути. Это будет больно!", "~/img/games/atomic_heart.jpg"),

            new Product("Elden ring", 3100, "Восстань, погасшая душа! Междуземье ждёт своего повелителя. Пусть благодать приведёт тебя к Кольцу Элден.\r\n\r\n• Огромный захватывающий мир\r\nОгромный мир с открытыми полями, множеством ситуаций и гигантскими подземельями, где сложные трёхмерные конструкции сочетаются воедино. Путешествуйте, преодолевайте смертельные опасности и радуйтесь успехам.\r\n\r\n• Создайте своего персонажа\r\nВы можете не только изменить внешность персонажа, но также комбинировать оружие, броню и предметы. Развивайте персонажа по своему вкусу. Наращивайте мышцы или постигайте таинства магии.\r\n\r\n• Эпическая драма, выросшая из мифа\r\nМногослойная история, разбитая на фрагменты. Эпическая драма, в которой мысли персонажей пересекаются в Междуземье.\r\n\r\n• Уникальный сетевой режим, приближающий вас к другим игрокам\r\nПомимо многопользовательского режима, в котором вы напрямую подключаетесь к другим игрокам и путешествуете вместе, есть асинхронный сетевой режим, позволяющий ощутить присутствие других игроков.", "~/img/games/elden_ring.jpeg")

        };

        public List<Product> GetAll()
        {
            return Products;
        }

        public Product TryGetById(int? id)
        {
            return Products.FirstOrDefault(product => product.Id == id);
        }

        public void RemoveById(int id)
        {
            Products.Remove(Products[id]);
        }

        public void Add(Product product)
        {
            Products.Add(product);
        }
    }
}
